mapper.select:
select p.id, p.name , p.brand_id , p.category1_id , p.category2_id , p.category3_id, p.unit_name,p.slider_urls , p.spec_value , p.status, p.audit_status , p.audit_message , p.create_time , p.update_time , p.is_deleted ,
       b.name brandName,c1.name category1Name,c2.name category2Name,c3.name category3Name
from product p
         left join brand b on p.brand_id=b.id
         left join category c1 on p.category1_id=c1.id
         left join category c2 on p.category2_id=c2.id
         left join category c3 on p.category3_id=c3.id


        and p.brand_id = #{brandId}


        and p.category1_id = #{category1Id}


        and p.category2_id = #{category2Id}


        and p.category3_id = #{category3Id}

    and p.is_deleted = 0
    order by p.id desc
